const initialState = {
    countries : []
}

function rootReducer(state=initialState, action) {
    switch(action.type){
        case 'GET_COUNTRIES':
            return {
                ...state,
                countries:action.payload
            }
            case 'FILTER_BY_CONTINENT':
                const allCountry =state.countries;
          
                let filtered; 
                if(action.payload !== 'All') filtered  = allCountry.filter((c) => c.continent === action.payload)
                return { 
                  ...state, 
                  countries: action.payload === 'All' ? allCountry: filtered
                }
            default: return state;
    }
}

export default rootReducer;
