import React from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getDetail } from "../actions";
import { useEffect } from "react";

export default function Detail(props){
    console.log(props)

    const dispatch= useDispatch()

    useEffect(() => {
        dispatch(getDetail(props.match.params.id))
    },[dispatch])

    const myCountry = useSelector((state) => state.countries);
    console.log(myCountry)

    return (
        <div>
            {
               myCountry.length > 0 ? 
               <div>
                <h1>Detalles</h1>
                <h2>Id : {myCountry[0].id}</h2>
                <h1>Pais : {myCountry[0].nombre}</h1>
                <h2>Continente : {myCountry[0].continente}</h2>
                <h2>Capital: {myCountry[0].capital}</h2>
                <h2>Area: {myCountry[0].area}</h2>
                <h2>Poblacion: {myCountry[0].population} habitantes.</h2>
                <h3>Actividades : {myCountry[0].Activities.map(el => el.nombre )}</h3>

                </div> : <p>Loading...</p>
           }
            <Link to='/home'><button>volver</button></Link>
        </div> 
        )}
