import React, { useEffect, useState } from 'react';
import { Link, useHistory} from 'react-router-dom';
import { postActivities, getActivities} from '../actions';
import { useDispatch , useSelector } from 'react-redux';

export default function ActivityCreate() {
  const dispatch = useDispatch();
  const activities = useSelector((state) => state.activities)

  const [input,setInput] = useState({
    nombre:"",
    dificultad:"",
    duracion:"",
    temporada:"",
    countries:[]
  })
  
  function handleChange(e){
    setInput({
      ...input,
      [e.target.name]: e.target.value
    })
  }

  function handleCheck(e){
    if(e.target.checked){
      setInput({
        ...input,
        temporada : e.target.value
      })
    }
  }

  useEffect(()=> {
    dispatch(getActivities())
  },[]);

  return(
    <div>
      <Link to='/home'><button>Volver</button></Link>
      <h1>Crea tu actividad!</h1>
      <form >
        <div>
          <label>Nombre : </label>
          <input type="text" value={input.name} name='nombre' placeholder='Nombre Aqui...' onChange={handleChange}/>
        </div>
        <div>
          <label>Dificultad : </label>
          <input type="number" value={input.dificultad} name='dificultad' placeholder='Dificualtad Aqui...' onChange={handleChange}/>
        </div>
        <div>
          <label>Duracion : </label>
          <input type="text" value={input.duracion} name='duracion' placeholder='Duracion Aqui...' onChange={handleChange}/>
        </div>
        <div>
          <label>Temporada </label>
          <label><input type="checkbox" name='Verano' value='Verano' onChange={handleCheck}/>Verano</label>
          <label><input type="checkbox" name='Invierno' value='Invierno' onChange={handleCheck}/>Invierno</label>
          <label><input type="checkbox" name='Otoño' value='Otoño' onChange={handleCheck}/>Otoño</label>
          <label><input type="checkbox" name='Primavera' value='Primavera' onChange={handleCheck}/>Primavera</label>
        </div>
          <select>
            {activities.map((act) =>(
              <option value={act.nombre}>{act.nombre}</option>
            ))}
          </select>

          <button type='submit'>Crear actividad</button>
        
      </form>
    </div>
  )


}
