const initialState = {
    countries : [],
    allCountries : []
}

function rootReducer(state=initialState, action) {
    switch(action.type){
        case 'GET_COUNTRIES':
            return {
                ...state,
                countries:action.payload,
                allCountries : action.payload
            }
        case 'FILTER_BY_CONTINENT':
            console.log('payloadCont ' +action.payload);
            const allCountry =state.allCountries;
            const continentFiltered = action.payload === 'All'? allCountries : allCountry.filter(el => el.continents[0] === action.payload)
            return{
                ...state,
                countries:continentFiltered
                
            }
            default: return state;
    }
}

export default rootReducer;
