import React from "react";
import {useState, useEffect} from 'react';
import { useDispatch, useSelector } from "react-redux";
import { getCountries } from "../actions";
import {Link} from 'react-router-dom';
import Card from "./Card";
import Paginado from "./Paginado";

export default function Home() {
    const dispatch = useDispatch();
    const allCountries = useSelector((state) =>state.countries);

    const [currentPage,setCurrenPage] = useState(1)

    const countriesPerPage = 10;

    let indexOfLastCount ;
    let indexOfFirstCountrie ;
    if(currentPage === 1){
        indexOfLastCountrie = 9;
        indexOfFirstCountrie = 0
    }else{
        indexOfFirstCountrie = ((currentPage -1)*10)-1
        indexOfLastCountrie ==((currentPage - 1)*10)+9
    }
    //  indexOfLastCountrie = currentPage * countriesPerPage
    //  indexOfFirstCountrie = indexOfLastCountrie - countriesPerPage


    const currentCountries = allCountries.slice(indexOfFirstCountrie, indexOfLastCountrie)
 
    const paginado = (pageNumber) => {
        setCurrenPage(pageNumber)
    }


    useEffect (() => {
        dispatch(getCountries())
    },[])

    function handleClick(e){
        e.preventDefault();
        dispatch(getCountries());
    }


    return (
        <div>
            <Link to = '/country'>Crear Country</Link>
            <h1>PAISES DEL MUNDO</h1>
            <button onClick={e =>{handleClick(e)}}>
                volver a cargar todos los paises
            </button>
            <div>
                <select>
                    <option value="asc">Ascendente</option>
                    <option value="asc">Descendente</option>
                </select>
               <select >
                    <option value="cont">Contienente</option>
                    <option value="act">Actividad</option>
               </select>
               
               <Paginado
               countriesPerPage={countriesPerPage}
               allCountries={allCountries.length}
               paginado={paginado}
               />
               
               
               {
                currentCountries ?.map((c) => {
                    return(
                        <div>                    
                        <Link to = {'/home'}>
                        <Card nombre = {c.name.common}  continente={c.continents} imagen = {c.flags[0]}/>
                        </Link>
                    </div>
                    )
               })
}
            </div>
        </div>
    )

}
