import React from "react";
import {useState, useEffect} from 'react';
import { useDispatch, useSelector } from "react-redux";
import { getCountries, filterCountryByContinent, filterActivity } from "../actions";
import {Link} from 'react-router-dom';
import Card from "./Card";
import Paginado from "./Paginado";

export default function Home() {
    const dispatch = useDispatch();
    const allCountries = useSelector((state) =>state.countries);

    const [currentPage, setCurrentPage] = useState(1);
    const countriesPerPage = 10;
  
    let indexOfLastCountry;
    let indexOfFirstCountry;
    if (currentPage === 1){
      indexOfLastCountry = 9;
      indexOfFirstCountry = 0
    }else{
      indexOfFirstCountry = ((currentPage - 1) * 10) - 1
      indexOfLastCountry = ((currentPage - 1) * 10) + 9
    }
    // indexOfLastCountry = currentPage * countriesPerPage; //20
    // indexOfFirstCountry = indexOfLastCountry - countriesPerPage; //10
    const currentCountries = allCountries.slice(
      indexOfFirstCountry,
      indexOfLastCountry
    );
 
    const paginado = (pageNumber) => {
        setCurrentPage(pageNumber)
    }


    useEffect (() => {
        dispatch(getCountries())
    },[])

    function handleClick(e){
        e.preventDefault();
        dispatch(getCountries());
    }

    function handleFilterContinent(e){
        dispatch(filterCountryByContinent(e.target.value))
    }

    function handleFilterActivity(e){
        e.preventDefault()
        dispatch(filterActivity(e.target.value))
        console.log('filterCountriesAct '+e.target.value)
    }

    return (
        <div>
            <Link to = '/country'>Crear Country</Link>
            <h1>PAISES DEL MUNDO</h1>
            <button onClick={e =>{handleClick(e)}}>
                volver a cargar todos los paises
            </button>
            <div>
                <select>
                    <option value="Asc">Ascendente</option>
                    <option value="Des">Descendente</option>
                </select>
               <select onChange={e => handleFilterContinent(e)}>
                    <option value="All">Todos</option>
                    <option value="Africa">Africa</option>
                    <option value="North America">North America</option>
                    <option value="South America">South America</option>
                    <option value="Asia">Asia</option>
                    <option value="Europe">Europe</option>
                    <option value="Oceania">Oceania</option>
                    
               </select>
               <select onChange={e => handleFilterActivity(e)}>
                    <option value="Act">Actividad</option>
                    <option value="All">Todos</option>
                    <option value="Created">Todos</option>
                    
                </select>
               
               <Paginado
               countriesPerPage={countriesPerPage}
               allCountries={allCountries.length}
               paginado={paginado}
               />
               
               
               {
                currentCountries ?.map((c) => {
                    return(
                        <div>                    
                        <Link to = {'/home'}>
                        <Card nombre = {c.name.common}  continente={c.continents} imagen = {c.flags[0]}/>
                        </Link>
                    </div>
                    )
               })
}
            </div>
        </div>
    )

}
