import React from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getDetail } from "../actions";
import { useEffect } from "react";

export default function Detail(props){
    console.log(props)

    const dispatch= useDispatch()

    useEffect(() => {
        dispatch(getDetail(props.match.params.id))
    },[dispatch])

    const myCountry = useSelector((state) => state.detail)

    return (
        <div>
            {
                myCountry.length > 0 ? 
                <div className="global">
                {console.log("hola")}
                        <div>
                        <img src={myCountry[2].imagen?myCountry[2].imagen : myCountry[2].image} alt="" width='500px' height='700px' />
                        <h1>{myCountry[1].nombre}</h1>
                        <h2>{myCountry[0].id}</h2>
                        <h2>{myCountry[4].capital}</h2>
                        <h2>{myCountry[5].subregion}</h2>
                        <h2>{myCountry[6].area}</h2>
                        <h2>{myCountry[7].population}</h2>

                        </div>
                      
                            <div className='infoActividad'>
                                <h1>Actividades: </h1>              
                                {
                                myCountry.Activities.length > 0? 
                                myCountry.Activities.map(a => 
                                    <ul>
                                    <h2>{a.name}</h2>
                                    </ul>
                                )
                                :
                                <h2>Sin actividades aun</h2>
                                }
                            </div> 

                    </div>: <p>Loading...</p>
           }
            <Link to='/home'><button>volver</button></Link>
        </div> 
        )}
