import React, { useEffect, useState } from 'react';
import { Link, useHistory} from 'react-router-dom';
import { postActivities, getActivities} from '../actions';
import { useDispatch , useSelector } from 'react-redux';


function validate(input){
  let errors = {};
  if(!input.name){
    errors.name = 'se requiere un nombre';
  }else if(!input.dificultad){
    errors.dificultad = 'Dificultad debe ser completada';
  }

  return errors;
}

export default function ActivityCreate() {
  const dispatch = useDispatch();
  const history = useHistory();
  const activities = useSelector((state) => state.activities)
  const continentes = useSelector((state) =>state.countries)

  const [errors,setErrors] = useState({});

  const [input,setInput] = useState({
    nombre:"",
    dificultad:"",
    duracion:"",
    temporada:"",
    countries:[]
  })
  
  function handleChange(e){

    setInput({
      ...input,
      [e.target.name]: e.target.value
    })
    setErrors(validate({
      ...input,
      [e.target.name]: e.target.value
    }))
    console.log(input)
  }

  function handleCheck(e){
    if(e.target.checked){
      setInput({
        ...input,
        temporada : e.target.value
      })
    }
  }

  function handleSelect(e){
    setInput({
      ...input,
      countries:[...input.countries,e.target.value]
    })
  }
  
  function handleSubmit(e){
    e.preventDefault();
    console.log(input)
    dispatch(postActivities(input))
    alert("Actividad Creada !!")
    setInput({
        nombre:"",
        dificultad:"",
        duracion:"",
        temporada:"",
        countries:[]
    })
    history.push('/home')
  }

  useEffect(()=> {
    dispatch(getActivities())
  },[]);


  function handleDelete(el){
    setInput({
      ...input,
      activities : input
    })
  }

  return(
    <div>
      <Link to='/home'><button>Volver</button></Link>
      <h1>Crea tu actividad!</h1>
      <form onSubmit={(e)=>handleSubmit(e)}>
        <div>
          <label>Nombre : </label>
            <input 
            type="text" 
            value={input.name} 
            name='name' 
            placeholder='Nombre Aqui...' 
            onChange={(e) => handleChange(e)}
            />
            {errors.name && (
                <p className='error'>{errors.name}</p>
            )}
        </div>
        <div>
          <label>Dificultad : </label>
          <input
           type="text"
           value={input.dificultad} 
           name='dificultad' 
           placeholder='Dificualtad Aqui...' 
           onChange={(e) => handleChange(e)}
           />
          {errors.dificultad && (
                <p className='error'>{errors.dificultad}</p>
            )}
        </div>
        <div>
          <label>Duracion : </label>
          <input type="text" value={input.duracion} name='duracion' placeholder='Duracion Aqui...' onChange={handleChange}/>
        </div>
        <div>
          <label>Temporada :</label>
          <input type="checkbox" name='Verano' value={input.temporada} onChange={(e) =>handleCheck(e)}/>Verano
          <label><input type="checkbox" name='Invierno' value={input.temporada} onChange={(e) =>handleCheck(e)}/>Invierno</label>
          <label><input type="checkbox" name='Otoño' value={input.temporada} onChange={(e) =>handleCheck(e)}/>Otoño</label>
          <label><input type="checkbox" name='Primavera' value={input.temporada} onChange={(e) =>handleCheck(e)}/>Primavera</label>
        </div>
          <select onChange={(e) => handleSelect(e)}>
            {activities.map((act) =>(
              <option value={act.nombre}>{act.nombre}</option>
            ))}
          </select>
          <ul><li>{input.countries.map(el => el + ' ,')}</li></ul>

          <button type='submit'>Crear actividad</button>
        
      </form>
    </div>
  )


}
