const initialState = {
    countries : [],
    allCountries : [],
    sortCountries: [],
    activities: [],
    

}

function rootReducer(state=initialState, action) {
    switch(action.type){
        case 'GET_COUNTRIES':
            return {
                ...state,
                countries:action.payload,
                allCountries : action.payload
            }
        case 'FILTER_BY_CONTINENT':
            console.log('payloadCont ' + action.payload);
            const allCountries =state.allCountries;
            const continentFiltered = action.payload === 'All'? allCountries : allCountries.filter(el => el.continente === action.payload)
            return{
                ...state,
                countries:continentFiltered
                
            }
        case 'GET_ACTIVITY':
            return {
                ...state,
                activities :action.payload,
                sortActivities : action.payload
            }

        case 'FILTER_ACTIVITY':
            const todos = state.allCountries;
            const createdFilter = action.payload === 'Created' ? state.countries.filter(el => el.createdActivity) : state.countries.filter(el => !el.createdActivity)
            return {
                ...state,
                countries : action.payload === 'All' ? state.countries : createdFilter
            }
            default: return state;
    }
}

export default rootReducer;







