import React from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getDetail } from "../actions";
import { useEffect } from "react";

export default function Detail(props){
    // console.log(props)

    const dispatch= useDispatch()

    useEffect(() => {
        dispatch(getDetail(props.match.params.id))
    },[dispatch])


    // const myCountry = useSelector(state => state.myCountry)[0];
    // console.log(myCountry);

    const myCountry = useSelector((state) => state.countries);
    console.log('hola')
    console.log(myCountry)

    // for(let i = 0; i < myCountry.length; i++ ){
    //     if(myCountry[i] === )
    // }

    return (
        <div>
            {
               myCountry.length > 0 ? 
               <div>
                <h1>Detalles</h1>
                <img src={myCountry.imagen}></img>
                <h2>Id : {myCountry.id}</h2>
                <h1>Pais : {myCountry.nombre}</h1>
                <h2>Continente : {myCountry.continente}</h2>
                <h2>Capital: {myCountry.capital}</h2>
                <h2>Area: {myCountry.area} kmÂ². </h2>
                <h2>Poblacion: {myCountry.population} habitantes.</h2>
                <h3>Actividades : {myCountry.Activities.map(el => el.nombre )}</h3>

                </div> : <p>Loading...</p>
           }
            <Link to='/home'><button>volver</button></Link>
        </div> 
        )}
