import React, { useEffect, useState } from 'react';
import { Link, useHistory} from 'react-router-dom';
import { postActivities, getActivities} from '../actions';
import { useDispatch , useSelector } from 'react-redux';

export default function ActivityCreate() {
  const dispatch = useDispatch();
  const activities = ((state) => state.activities)

  const [input,setInput] = useState({
    nombre:"",
    dificultad:"",
    duración:"",
    temporada:"",
    countries:[]
  })

  useEffect(()=> {
    dispatch(getActivities())
  },[]);

  return(
    <div>
      <Link to='/home'><button>Volver</button></Link>
      <h1>Crea tu actividad!</h1>
      <form action="">
        <div>
          <label>Nombre : </label>
          <input type="text" value={input.name} name='name' />
        </div>
        <div>
          <label>Dificultad : </label>
          <input type="number" value={input.dificultad} name='dificultad' placeholder='Nombre Aqui...'/>
        </div>
        <div>
          <label>Duración(Horas) : </label>
          <input type="text" value={input.duración} name='duración' placeholder='Duracion Aqui...'/>
        </div>
        <div>
          <label>Temporada </label>
          <label><input type="checkbox" name='Verano' value='Verano' />Verano</label>
          <label><input type="checkbox" name='Invierno' value='Invierno' />Invierno</label>
          <label><input type="checkbox" name='Otoño' value='Otoño' />Otoño</label>
          <label><input type="checkbox" name='Primavera' value='Primavera' />Primavera</label>
        </div>




        <div>


            
          <label className={s.label}>Paises: </label>
          <select className={s.selectForm} name='countries' onChange={handleCountriesSelected} >
            <option >Select the countries of the activity...</option>
            {countries?.map(c => (
              <option key={c.id} value={c.id}>{c.name}</option>
            ))}   
          </select>
          {errors.countries && (<p className={s.error}> {errors.countries}</p>)}          
          </div>
        




      </form>
    </div>
  )


}
